import React, { useEffect, useRef } from 'react';
import './Portfolio.css';

const Portfolio = () => {
  const portfolioRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('visible');
          }
        });
      },
      { threshold: 0.1 }
    );

    if (portfolioRef.current) {
      observer.observe(portfolioRef.current);
    }

    return () => observer.disconnect();
  }, []);

  const projects = [
    {
      title: 'E-handelsplattform',
      category: 'Webbutveckling',
      description: 'Modern e-handelsl√∂sning med React och Node.js',
      image: 'üõí',
      status: 'Slutf√∂rd'
    },
    {
      title: 'F√∂retagswebbplats',
      category: 'Design & Utveckling',
      description: 'Responsiv webbplats med fokus p√• anv√§ndarupplevelse',
      image: 'üè¢',
      status: 'Slutf√∂rd'
    },
    {
      title: 'Mobilapp',
      category: 'UX/UI Design',
      description: 'Native mobilapplikation f√∂r iOS och Android',
      image: 'üì±',
      status: 'P√•g√•ende'
    },
    {
      title: 'Branding Projekt',
      category: 'Design',
      description: 'Komplett varum√§rkesidentitet och visuell profil',
      image: 'üé®',
      status: 'Slutf√∂rd'
    },
    {
      title: 'SaaS Plattform',
      category: 'Webbutveckling',
      description: 'Skalbar molnbaserad applikation',
      image: '‚òÅÔ∏è',
      status: 'Planerad'
    },
    {
      title: 'Digital Transformation',
      category: 'Strategi',
      description: 'Digitalisering av aff√§rsprocesser',
      image: 'üîÑ',
      status: 'P√•g√•ende'
    }
  ];

  return (
    <section id="portfolio" className="portfolio section" ref={portfolioRef}>
      <div className="container">
        <h2 className="section-title">V√•ra projekt</h2>
        <p className="section-subtitle">
          H√§r ser du n√•gra exempel p√• projekt vi arbetat med och som vi √§r stolta √∂ver
        </p>
        <div className="portfolio-grid">
          {projects.map((project, index) => (
            <div key={index} className="project-card card">
              <div className="project-image">
                <div className="project-icon">{project.image}</div>
                <div className={`project-status status-${project.status.toLowerCase().replace('√•', 'a')}`}>
                  {project.status}
                </div>
              </div>
              <div className="project-content">
                <div className="project-category">{project.category}</div>
                <h3 className="project-title">{project.title}</h3>
                <p className="project-description">{project.description}</p>
              </div>
            </div>
          ))}
        </div>
        <div className="portfolio-cta">
          <p>Vill du se fler projekt eller diskutera ditt kommande projekt?</p>
          <button 
            className="btn btn-primary"
            onClick={() => document.getElementById('contact').scrollIntoView({ behavior: 'smooth' })}
          >
            Kontakta oss
          </button>
        </div>
      </div>
    </section>
  );
};

export default Portfolio;
