import React, { useEffect, useRef } from 'react';
import './About.css';

const About = () => {
  const aboutRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('visible');
          }
        });
      },
      { threshold: 0.1 }
    );

    if (aboutRef.current) {
      observer.observe(aboutRef.current);
    }

    return () => observer.disconnect();
  }, []);

  const values = [
    {
      icon: 'üí°',
      title: 'Innovation',
      description: 'Vi h√•ller oss alltid uppdaterade med de senaste teknologierna och trenderna f√∂r att leverera moderna l√∂sningar.'
    },
    {
      icon: '‚≠ê',
      title: 'Kvalitet',
      description: 'Varje projekt genomf√∂rs med h√∂gsta kvalitet och noggrannhet f√∂r att s√§kerst√§lla b√§sta m√∂jliga resultat.'
    },
    {
      icon: 'ü§ù',
      title: 'L√•ngsiktighet',
      description: 'Vi bygger l√•ngsiktiga partnerskap med v√•ra klienter och fokuserar p√• h√•llbara digitala l√∂sningar.'
    }
  ];

  return (
    <section id="about" className="about section" ref={aboutRef}>
      <div className="container">
        <div className="about-content">
          <div className="about-text">
            <h2 className="section-title">Om oss</h2>
            <p className="section-subtitle">
              Vi √§r en passionerad grupp av utvecklare, designers och strateger som 
              √§lskar att skapa digitala upplevelser som g√∂r skillnad.
            </p>
            <div className="about-description">
              <p>
                P√• Digitorn kombinerar vi kreativitet med teknisk expertis f√∂r att 
                hj√§lpa f√∂retag att v√§xa i den digitala v√§rlden. V√•rt team har √∂ver 
                10 √•rs erfarenhet av webbutveckling, design och digital strategi.
              </p>
              <p>
                Vi tror p√• att varje projekt √§r unikt och kr√§ver en skr√§ddarsydd 
                approach. Fr√•n f√∂rsta id√© till f√§rdig produkt arbetar vi n√§ra 
                v√•ra klienter f√∂r att s√§kerst√§lla att vi levererar exakt det de beh√∂ver.
              </p>
            </div>
          </div>
          <div className="about-values">
            {values.map((value, index) => (
              <div key={index} className="value-card card">
                <div className="value-icon">{value.icon}</div>
                <h3 className="value-title">{value.title}</h3>
                <p className="value-description">{value.description}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default About;
