import React, { useEffect, useRef } from 'react';
import './Services.css';

const Services = () => {
  const servicesRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('visible');
          }
        });
      },
      { threshold: 0.1 }
    );

    if (servicesRef.current) {
      observer.observe(servicesRef.current);
    }

    return () => observer.disconnect();
  }, []);

  const services = [
    {
      icon: 'üíª',
      title: 'Webbutveckling',
      description: 'Moderna, responsiva webbplatser och webbapplikationer byggda med senaste teknologier.',
      features: ['React & Next.js', 'Responsiv design', 'SEO-optimering', 'S√§kerhet & prestanda']
    },
    {
      icon: 'üé®',
      title: 'Design',
      description: 'Visuell identitet och grafisk design som f√∂rst√§rker ditt varum√§rke och budskap.',
      features: ['Logotyp & varum√§rke', 'Grafisk design', 'Printmaterial', 'Digitala medier']
    },
    {
      icon: 'üì±',
      title: 'UX/UI Design',
      description: 'Anv√§ndarcentrerad design som skapar intuitiva och engagerande digitala upplevelser.',
      features: ['Anv√§ndarresearch', 'Wireframing', 'Prototyping', 'Anv√§ndbarhetstestning']
    },
    {
      icon: 'üöÄ',
      title: 'Digital Strategi',
      description: 'Strategisk r√•dgivning och planering f√∂r att maximera din digitala n√§rvaro.',
      features: ['Digital strategi', 'Marknadsf√∂ring', 'Analys & rapportering', 'Optimering']
    }
  ];

  return (
    <section id="services" className="services section" ref={servicesRef}>
      <div className="container">
        <h2 className="section-title">Vad vi g√∂r</h2>
        <p className="section-subtitle">
          Vi erbjuder en komplett palett av digitala tj√§nster f√∂r att hj√§lpa ditt f√∂retag att v√§xa
        </p>
        <div className="services-grid">
          {services.map((service, index) => (
            <div key={index} className="service-card card">
              <div className="service-icon">{service.icon}</div>
              <h3 className="service-title">{service.title}</h3>
              <p className="service-description">{service.description}</p>
              <ul className="service-features">
                {service.features.map((feature, featureIndex) => (
                  <li key={featureIndex} className="service-feature">
                    <span className="feature-check">‚úì</span>
                    {feature}
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Services;
